function get_filter($path)
{
	$cnt = query($path."/count");
	foreach ($path."/entry")
	{
		if ($InDeX > $cnt) break;
		$enable = query("enable");
		$string = query("string");
		if ($enable==1 && $string!="") $filter = $filter.$string."/";
	}
	if ($filter!="") $filter = "/".$filter;
	return $filter;
}

