// get_dns function concatenates DNS values
function get_dns($p)
{
    anchor($p);
    $cnt = query("dns/count")+0;
    foreach ("dns/entry")
    {
        if ($InDeX > $cnt) break;
        if ($dns=="") $dns = $VaLuE;
        else $dns = $dns." ".$VaLuE;
    }
    return $dns;
}

