runit() {
    if test "$showonly"
    then
        cat
    else
        (
            echo '('
            echo 'exec <&3'     # regain stdin
            cat
            echo ');'
        ) | ash $shopts |
            awk "/^= / { exit \$2 } $logfilter { print }"
    fi
}

/^include[ \t]+/ {
	orig = $0
	sub(/[ \t]+#.*$/, "")
	if (NF != 2) {
		msg = "(" FILENAME ", line " lineno ")"
		msg = msg " include syntax error in \"" orig "\""
		print "#:" msg
		exit 1
	}
	newfile = $2
	if (newfile !~ /^\// && FILENAME ~ /\//) {
		prefix = FILENAME
		sub("[^/]+$", "", prefix)
		newfile = prefix newfile
	}
	system("ipsec _include " newfile)
	print ""
	print "#>", FILENAME, lineno + 1
	next
}

klipsinterface() {
	# pull apart the interface spec
	virt=`expr $1 : '\([^=]*\)=.*'`
	phys=`expr $1 : '[^=]*=\(.*\)'`

	# ...

	# figure out config for interface
	phys_addr=
	eval `getinterfaceinfo $phys phys_`
	if test " $phys_addr" = " "
	then
		echo "unable to determine address of \`$phys'"
		exit 1
	fi
	# ...
}

getinterfaceinfo() {
	ip addr show dev $1 | awk '
	BEGIN {
		MTU=""
		TYPE="unknown"
	}
	/BROADCAST/   { TYPE="broadcast" }
	/POINTOPOINT/ { TYPE="pointtopoint" }
	/mtu/ {
			sub("^.*mtu ", "", $0)
			MTU=$1
		}
	$1 == "inet" || $1 == "inet6" {
			split($2,addr,"/")
			other=""
			if ($3 == "peer")
				other=$4
			print "'$2'type=" TYPE
			print "'$2'addr=" addr[1]
			print "'$2'mask=" addr[2]
			print "'$2'otheraddr=" other
			print "'$2'mtu=" MTU
			exit 0
		}'
}

