  "type": "object",
  "properties": {
    "target_key": {
      "type": "string",
      "description": "要查询唯一值的发现记录中的字段名称（例如，'file_path'、'link_identifiers'、'notes'）。"
    }
  },
  "required": [
    "target_key"
  ]
}

