{
  "type": "object",
  "properties": {
    "command": {
      "type": "string",
      "description": "要执行的 shell 命令。命令将在 'openvpn' 目录中执行。命令中的所有路径应相对于此目录。"
    }
  },
  "required": [
    "command"
  ]
}

