  "type": "object",
  "properties": {
    "task": {
      "type": "string",
      "description": "要执行的子任务的详细描述。请指定分析对象。"
    },
    "run_in_background": {
      "type": "boolean",
      "description": "是否在后台运行此任务。",
      "default": false
    }
  },
  "required": [
    "task"
  ]
}

