  "type": "object",
  "properties": {
    "tasks": {
      "type": "array",
      "items": {
        "type": "string",
        "description": "要执行的子任务的详细描述。每个任务描述都是独立的，应指定分析对象。"
      },
      "description": "要分配给子代理并行执行的独立任务描述列表。"
    },
    "run_in_background": {
      "type": "boolean",
      "description": "是否在后台运行此任务。",
      "default": false
    }
  },
  "required": [
    "tasks"
  ]
