  "type": "object",
  "properties": {
    "query_key": {
      "type": "string",
      "description": "要查询的发现记录中的字段名称。",
      "enum": [
        "file_path",
        "link_identifiers",
        "notes"
      ]
    },
    "query_value": {
      "description": "要匹配的值。对于 'link_identifiers' 列表，这应该是单个关键字字符串；对于其他字段，将执行直接相等比较。"
    }
  },
  "required": [
    "query_key",
    "query_value"
  ]
}

