    },
    "status": {
      "type": "string",
      "enum": [
        "continue",
        "complete"
      ],
      "description": "必须是 'continue'（如果选择了工具）或 'complete'（如果选择了 'finish'）。"
    }
  },
  "required": [
    "thought",
    "action",
    "action_input",
    "status"
  ]
}

