      },
      "description": "要分配给子代理并行执行的独立任务描述列表。"
    },
    "run_in_background": {
      "type": "boolean",
      "description": "是否在后台运行此任务。",
      "default": false
    }
  },
  "required": [
    "tasks"
  ]
