    *ppiVar4 = piVar1;
    if (*ppiVar4 == NULL) {
        sym.imp.__assert(0xaf50 | 0x10000,0xad88 | 0x10000,0x3eb,0xb1d0 | 0x10000);
    }
    sym.imp.memset(*ppiVar4,0,0x1c);
    (*ppiVar4)[2] = ppiVar4[-3];
