	{
		return false;
	}


	var APLen;
	var localKey;
  if (wlanPara[20] == 1)
  		localKey = cf.key1.value;
  else if (wlanPara[20] == 2)
  			localKey = cf.key2.value;
  		else if (wlanPara[20] == 3)
